var CFAccordion=function(elem,mode){"use strict";this.el=document.getElementById(elem);this.elems=[];this.cons=[];this.heightArray=[];this.openArray=[];this.mode=mode;this.init();};CFAccordion.prototype.init=function(){"use strict";this.elems=this.getLevelElements(this.el,'h3');for(var i=0;i<this.elems.length;i++){this.setListener(i);}
this.cons=this.getLevelElements(this.el,'div');for(var j=0;j<this.cons.length;j++){this.heightArray.push(this.cons[j].offsetHeight);try{this.cons[j].style.height="0px";}catch(e){this.cons[j].style.setProperty('height','0px');}}};CFAccordion.prototype.setListener=function(elid){"use strict";var that=this;var m=this.elems[elid];m.addEventListener("click",function(){that.openTab(elid);},false);};CFAccordion.prototype.openTab=function(elid){"use strict";var that=this;if(this.openArray.indexOf(elid)===-1){if(this.mode==='dynamic'){for(var i=0;i<this.cons.length;i++){try{this.cons[i].style.height="0px";}catch(e){this.cons[i].style.setProperty('height','0px');}
this.elems[i].className="";this.openArray.splice(this.openArray.indexOf(elid),1);}}
try{this.cons[elid].style.height=this.heightArray[elid]+"px";}catch(e){this.cons[elid].style.setProperty('height',this.heightArray[elid]+'px');}
this.elems[elid].className="opentab";this.openArray.push(elid);}else{try{this.cons[elid].style.height="0px";}catch(e){this.cons[elid].style.setProperty('height','0px');}
this.elems[elid].className="";this.openArray.splice(this.openArray.indexOf(elid),1);}};CFAccordion.prototype.initialOpen=function(elid){"use strict";var that=this;that.openTab(elid);};CFAccordion.prototype.collapseAll=function(elid){"use strict";for(var i=0;i<this.cons.length;i++){try{this.cons[i].style.height='0px';}catch(e){this.cons[i].style.setProperty('height','0px');}
this.elems[i].className='';this.openArray.splice(this.heightArray[elid]+'px');}};CFAccordion.prototype.expandAll=function(){"use strict";for(var i=0;i<this.cons.length;i++){try{this.cons[i].style.height=this.heightArray[i]+'px';}catch(e){this.cons[i].style.setProperty('height',this.heightArray[i]+'px');}
this.elems[i].className="opentab";this.openArray.push(i);}};CFAccordion.prototype.getLevelElements=function(el,tag){"use strict";var arr=el.getElementsByTagName(tag);var items=[];for(var i=0;i<arr.length;i++){if(arr[i].parentNode===el){items.push(arr[i]);}}
return items;};